mapper.select:
SELECT ud.id AS doctorid, ud.`name` AS doctorname, bo.`name` AS officename, sq.gender, bh.`name` AS hospitalname, sq.gmt_create FROM ser_inquiry sq, us_doctor ud, bas_office bo, bas_hospital bh
sq.doctor_id = ud.id AND ud.hos_id = bh.id AND ud.hos_id = bh.id AND sq.`status` = 3 AND sq.is_del = 0
AND bo.id = #{officeid}
AND sq.patient_id = #{patientid}
AND bh.`name` LIKE CONCAT('%',#{hospitalNname},'%')
AND sq.gmt_create BETWEEN #{startDate} AND #{endDate}

SELECT COUNT(*) FROM ( SELECT ud.id AS doctorid, ud.`name` AS doctorname, bo.`name` AS officename, sq.gender, bh.`name` AS hospitalname, sq.gmt_create FROM ser_inquiry sq, us_doctor ud, bas_office bo, bas_hospital bh
sq.doctor_id = ud.id AND ud.hos_id = bh.id AND ud.hos_id = bh.id AND sq.`status` = 3 AND sq.is_del = 0
AND bo.id = #{officeid}
AND sq.patient_id = #{patientid}
AND bh.`name` LIKE CONCAT('%',#{hospitalNname},'%')
AND sq.gmt_create BETWEEN #{startDate} AND #{endDate}
) total
